INSERT INTO composition (name, date, ensemble_id, description) VALUES ('Knockin’ on Heaven’s Door', '1973-07-13', currval('ensemble_id_seq'), 'Knockin’ on Heaven’s Door («Постучать в небесную дверь», иносказательно «Быть на пороге смерти») — известная песня Боба Дилана, написанная для саундтрека к фильму «Пэт Гэрретт и Билли Кид». Со временем она была признана классической в творчестве музыканта, подтверждением чему является её неоднократное участие в сборниках лучших песен Боба Дилана. Также широкую известность Knockin’ on Heaven’s Door получила благодаря своим кавер-версиям.');
INSERT INTO composition (name, date, ensemble_id, description) VALUES ('Like a Rolling Stone', '1965-07-20', currval('ensemble_id_seq'), '«Like a Rolling Stone» (с англ. — «Как перекати-поле») — песня американского автора-исполнителя Боба Дилана. Содержание композиции основано на стихотворении, написанном Диланом в июне 1965 года, после возвращения из изнурительного турне по Англии (англ.)русск.. Музыкант переработал стихотворение в песню, разделив её на четыре куплета и припев. Несколько недель спустя «Like a Rolling Stone» была записана во время сессий для шестого альбома Highway 61 Revisited.');
INSERT INTO composition (name, date, ensemble_id, description) VALUES ('Like a Rolling Stone Cover', '1965-07-20', currval('ensemble_id_seq'), '«Like a Rolling Stone» (с англ. — «Как перекати-поле») — песня американского автора-исполнителя Боба Дилана. Содержание композиции основано на стихотворении, написанном Диланом в июне 1965 года, после возвращения из изнурительного турне по Англии (англ.)русск.. Музыкант переработал стихотворение в песню, разделив её на четыре куплета и припев. Несколько недель спустя «Like a Rolling Stone» была записана во время сессий для шестого альбома Highway 61 Revisited.');