INSERT INTO ensemble (name, type, description) VALUES ('Led Zeppelin', 'quartet', 'Led Zeppelin (рус. Лед Зе́ппелин[2][3], МФА: [ˌlɛd ˈzɛpˌlɪn][4]) — британская рок-группа, образовавшаяся в сентябре 1968 года в Лондоне, и признанная одной из самых успешных, новаторских и влиятельных в современной истории[5]. Создав собственное звучание (для которого были характерны утяжелённый гитарный драйв, оглушающее звучание ритм-секции и пронзительный вокал), Led Zeppelin стали одной из ведущих групп хард-рока[6], сыграли основополагающую роль в становлении хеви-метала[1][7][8], свободно интерпретируя фолк- и блюз-классику и обогащая стиль элементами других музыкальных жанров (рокабилли, рэгги, соул, фанка, кантри)[9]. Остаётся одной из наиболее успешных групп в рок-музыке: общемировой тираж альбомов превышает 300 миллионов[10][11][12], 112 миллионов было продано в США (четвёртое место)[13]. Семь альбомов группы поднимались на вершину Billboard 200. Занимает первое место в списке VH1 «100 величайших артистов хард-рока»[14]. Журнал Rolling Stone признал коллектив «самой тяжёлой группой» и «лучшей группой 70-х»[15]. В 1995 году Led Zeppelin были введены в Зал славы рок-н-ролла, в 2005 году получили Грэмми за вклад на протяжении всей карьеры (Grammy Lifetime Achievement Award)[5], в мае 2006 года — Polar Music Prize[10].');
INSERT INTO musician (name, date, sex, description) VALUES ('Роберт Плант', '1948-08-20', true, 'Ро́берт Э́нтони Плант (англ. Robert Anthony Plant, 20 августа 1948, Уэст-Бромидж, Стаффордшир, Англия) — британский рок-вокалист, известный прежде всего участием в Led Zeppelin. После распада группы Плант начал успешную сольную карьеру, которая продолжается по сей день. Командор ордена Британской империи (CBE).');
