INSERT INTO musician (name, date, sex, description) VALUES ('Джимми Пейдж', '1944-01-09', true, 'Британский рок-музыкант, аранжировщик, композитор, музыкальный продюсер и гитарист-виртуоз, стоявший у истоков Led Zeppelin и до самого конца остававшийся музыкальным «мозгом» группы. До этого был известен как сессионный гитарист и участник The Yardbirds (c конца 1966 до 1968 года).');
