INSERT INTO musician (name, date, sex, description) VALUES ('Джон Пол Джонс', '1946-01-03', true, 'Джон Пол Джонс (англ. John Paul Jones; настоящее имя Джон Ри́чард Бо́лдуин (John Richard Baldwin); 3 января 1946 года, Сидкап (англ.)русск., Кент, Англия) — британский музыкант-мультиинструменталист, музыкальный продюсер, композитор, аранжировщик. Наиболее известен как басист и клавишник группы Led Zeppelin. В 2011 году признан одним из лучших бас-гитаристов всех времён согласно опросу, проведённому журналом Rolling Stone. Также занял 14-е место в списке 50 величайших басистов всех времён по версии редакции того же журнала.');
