INSERT INTO musician (name, date, sex, description) VALUES ('Стив Морс', '1954-07-28', true, 'Стивен Джей Морс (англ. Steven J. Morse; род. 28 июля 1954, Гамильтон, Огайо, США) — американский гитарист, автор песен, пилот. Несколько раз был признан лучшим гитаристом года по версии журнала «Guitar Player». Играет в группе «Deep Purple» c 1994 года, а также «Steve Morse Band». В 2009 году журналом Classic Rock включен в список величайших гитаристов всех времен.');
