INSERT INTO musician (name, date, sex, description) VALUES ('Дон Эйри', '1948-06-21', true, 'Дон Э́йри (англ. Don Airey, полное имя — Дональд Смит Эйри, англ. Donald Smith Airey; род. 21 июня 1948, Сандерленд, Англия) — британский пианист, органист, композитор, аранжировщик. Получил известность, сотрудничая со многими известными группами в качестве сессионного музыканта. Наиболее известен как многолетний клавишник «Deep Purple». Старший брат гитариста Кита Эйри (англ. Keith Airey, «The Zombies»).');
