INSERT INTO composition (name, date, ensemble_id, description) VALUES ('Smoke on the Water', '1972-11-08', currval('ensemble_id_seq'), 'Smoke on the Water (с англ. — «Дым над водой») — песня рок-группы Deep Purple, записанная в декабре 1971 года и впервые выпущенная в альбоме Machine Head в марте 1972 года. В качестве сингла она вышла лишь в 1973 году; вторую сторону сингла занимала её концертная версия из альбома Made in Japan.');